    var middle = wordRegexp(middleKeywords);
    var closing = wordRegexp(endKeywords);
    var doubleClosing = wordRegexp(['xxxxzzzz']);
    var doOpening = wordRegexp(['xxxxzzzz']);
    var noIndentWords = wordRegexp(['xxxxzzzz']);
